package com.Assignmentoperators;

public class Assignmentop_code {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4;
		int res;
		res=a;
		System.out.println("var using ="+res);
		res+=a;
		System.out.println("var using +=" + res);
		res*=a;
		System.out.println("var using *=" + res);

	}

}
